}","allowCamera",false,"allowModals",true,"showInEditor",true,"iframeCode","<head>
  <style> body { margin: 0; } </style>

<!--   <script src="https://cdn.tryretool.com/js/react.production.min.js" crossorigin></script>
  <script src="https://cdn.tryretool.com/js/react-dom.production.min.js" crossorigin></script> -->

  <script src="https://unpkg.com/babel-standalone"></script>

  <script src="//unpkg.com/react/umd/react.production.min.js" crossorigin></script>
  <script src="//unpkg.com/react-dom/umd/react-dom.production.min.js" crossorigin></script>

  <script src="https://unpkg.com/@material-ui/core@3.9.3/umd/material-ui.production.min.js"></script>

  <script src="//unpkg.com/react-force-graph-2d"></script>

<!--   <script src="https://unpkg.com/3d-force-graph@1.70.9/dist/3d-force-graph.min.js"></script>
  <script src="https://unpkg.com/react-force-graph-3d"></script> -->
</head>
<body>
  <div id="graph"></div>

  <script type="text/babel">
    const { useState, useMemo, useEffect, useCallback } = React;
    const { CircularProgress, Dialog, DialogTitle, DialogContent, DialogContentText, Table, TableHead, TableBody, TableRow, TableCell } = window["material-ui"];

		const NODE_R = 6;

    const capitalizeFirstLetter = (string) => {
      return string.charAt(0).toUpperCase() + string.slice(1);
    }

    const Graph = ({triggerQuery, model, modelUpdate}) => {
      const [currentNode, setCurrentNode] = useState(null);
      const [highlightNodes, setHighlightNodes] = useState(new Set());
      const [highlightLinks, setHighlightLinks] = useState(new Set());
      const [hoverNode, setHoverNode] = useState(null);

      const updateHighlight = () => {
        setHighlightNodes(highlightNodes);
        setHighlightLinks(highlightLinks);
      };

      const handleNodeHover = node => {
        highlightNodes.clear();
        highlightLinks.clear();
        if (node) {
          highlightNodes.add(node);
          node.neighbors.forEach(neighbor => highlightNodes.add(neighbor));
          node.links.forEach(link => highlightLinks.add(link));
        }

        setHoverNode(node || null);
        updateHighlight();
      };

      const handleLinkHover = link => {
        highlightNodes.clear();
        highlightLinks.clear();

        if (link) {
          highlightLinks.add(link);
          highlightNodes.add(link.source);
          highlightNodes.add(link.target);
        }

        updateHighlight();
      };

      const nodeCanvasObject = (node, ctx, globalScale) => {
        if (highlightNodes.has(node))
        	paintRing(node, ctx);

        if (node.type === "times" || node.type === "plus") {
          const label = node.type === "times" ? 'X' : '+';
          const fontSize = (node.type === "times" ? 11 : 16)/globalScale;
          ctx.font = `${fontSize}px Sans-Serif`;
          const textWidth = ctx.measureText(label).width;

          ctx.beginPath();
          ctx.arc(node.x, node.y, NODE_R, 0, 2 * Math.PI, false);
          ctx.fillStyle = getColor(node.type, node.value);
          ctx.fill();

          const bckgDimensions = [NODE_R, NODE_R]
          ctx.fillRect(node.x - bckgDimensions[0] / 2, node.y - bckgDimensions[1] / 2, ...bckgDimensions);

          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.fillStyle = 'white';
          ctx.fillText(label, node.x, node.y);
        }
      }

      const paintRing = useCallback((node, ctx, globalScale) => {
        // add ring just for highlighted nodes
        ctx.beginPath();
        ctx.arc(node.x, node.y, NODE_R * 1.4, 0, 2 * Math.PI, false);
        ctx.fillStyle = node === hoverNode ? 'purple' : 'orange';
        ctx.fill();
      }, [hoverNode]);

      const getId = givenId => {
        return model.graph_data.nodes.filter(node => {
          return node.id === givenId;
        })[0]
      }

      const data = useMemo(() => {
          // cross-link node objects
          model.graph_data.links.forEach(link => {
            const a = getId(link.source);
            const b = getId(link.target);
            !a.neighbors && (a.neighbors = []);
            !b.neighbors && (b.neighbors = []);
            a.neighbors.push(b);
            b.neighbors.push(a);

            !a.links && (a.links = []);
            !b.links && (b.links = []);
            a.links.push(link);
            b.links.push(link);
          });

        return model.graph_data;
      }, [model]);

      const getColor = (type, value) => {
        if (type === "times")
          return "pink"
        if (type === "plus")
          return "cyan"
        if (type === "input") {
          const max = model.max_shapely_value;
          const percent = Math.floor(value >= max ? 100 : (value/max * 100 + 35));
          const red = Math.floor(value >= max/2 ? 255 : value/max * 256.0);
          const green = Math.floor(value <= max/2 ? 255 : (max-value)/max * 256.0);
          return value === 0 ? "white" :`rgb(${red} ${green} 0 / ${percent}%)`;
        }
        return "white"
      }
      return (
        <div>
          <ForceGraph2D
            graphData={data}
       		  nodeRelSize={NODE_R}
            autoPauseRedraw={false}
            linkWidth={link => highlightLinks.has(link) ? 5 : 1}
            linkDirectionalParticles={4}
            linkDirectionalParticleWidth={link => highlightLinks.has(link) ? 4 : 0}
            nodeCanvasObjectMode={node => (node.type === "times" || node.type === "plus") ? "after" : highlightNodes.has(node) ? 'before' : undefined}
            nodeCanvasObject={nodeCanvasObject}
            onNodeHover={handleNodeHover}
            onLinkHover={handleLinkHover}
            nodeColor={(node) => getColor(node.type, node.shapley_value)}
            onNodeClick={(node, prevNode) => {
              setCurrentNode({id: node.id, data: node.data, table_name: node.table_name, type: node.type, shapley_value: node.shapley_value});
            }}
            nodeLabel={(node) => (node.type === "input" ? node.shapley_value : capitalizeFirstLetter(node.type))}
            // nodeVal={(node) => (node.type === "input" ? node.shapley_value : 1)}
            onNodeDragEnd={node => {
              node.fx = node.x;
              node.fy = node.y;
              node.fz = node.z;
            }}
          />
          <Dialog
            open={Boolean(currentNode)}
            onClose={() => setCurrentNode(null)}
            keepMounted
            aria-describedby="alert-dialog-slide-description"
          >
            {currentNode && (currentNode.type === "input" ? <div>
              <DialogTitle>{currentNode.table_name}</DialogTitle>
              <DialogContent>
                <DialogContentText id="dialog-description">
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>key</TableCell>
                        <TableCell>value</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      <TableRow key={-2}>
                        <TableCell component="th" scope="row">Node ID</TableCell>
                        <TableCell>{currentNode.id}</TableCell>
                      </TableRow>
                      <TableRow key={-1}>
                        <TableCell component="th" scope="row">Shapley Value</TableCell>
                        <TableCell>{currentNode.shapley_value}</TableCell>
                      </TableRow>
                      {Object.keys(currentNode.data).map((key, index) => (
                        <TableRow key={index}>
                          <TableCell component="th" scope="row">{key}</TableCell>
                          <TableCell>{currentNode.data[key]}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </DialogContentText>
              </DialogContent>
            </div> : <div>
             <DialogTitle>{capitalizeFirstLetter(currentNode.type)} type node</DialogTitle>
              <DialogContent>
                <DialogContentText id="type-dialog-description">
                  node id: {currentNode.id}
                </DialogContentText>
              </DialogContent>
           </div>)}
          </Dialog>
        </div>
      );
    }

    const ConnectedComponent = Retool.connectReactComponent(Graph);

    ReactDOM.render(
      <ConnectedComponent/>,
      document.getElementById('graph')
    );
  </script>
</body>
