const columns = {{heat_map.columns}}

const data = {{heat_map.data}}
const get_max = (col_name) => _.max(data.map(val => val[col_name])) //max value of column for color proportion

// const getColor = (value, max) => {
//   const percent = Math.floor(value >= max ? 100 : (value/max * 100 + 35)) //opacity %
//   const red = Math.floor(value >= max/2 ? 255 : value/max * 256.0) //red amount
//   const green = Math.floor(value <= max/2 ? 255 : (max-value)/max * 256.0) //green amount
//   return value === 0 ? `white` :`rgb(${red} ${green} 0 / ${percent}%)`
// }

const getColorFunction = (maxValue) => {
  const mapper = '{\{((value) => {const max = ' + `${maxValue}` + ';const percent = 30;const red = Math.floor(value >= max/2 ? 255 : value/max * 256.0);const green = Math.floor(value <= max/2 ? 255 : (max-value)/max * 256.0);return  value === 0 ? `white` :`rgb(${red} ${green} 0 / ${percent}%)`;})(self)}\}'
  if (_.isNumber(maxValue))
    return {colorMapper: mapper}
  else
    return {}
}

return _.map(columns, (value, key) => ({name: value, columnAlignment: 'center', columnFrozenAlignments: 'center', ...getColorFunction(get_max(value))})) //insert the color function into the table settings

